  class hook167 extends _HOOK_CLASS_
  {

    /* !Hook Data - DO NOT REMOVE */
    public static function hookData()
    {
      return array_merge_recursive(array(
        'member' =>
        array(
          0 =>
          array(
            'selector' => 'li.ipsStreamItem_member > div.ipsStreamItem_container > div.ipsStreamItem_header > p',
            'type' => 'add_after',
            'content' => '
{{if settings.aXenGroupSearch}}
  {{if $member->aXenSGISortedGroups()}}
    {{foreach $member->aXenSGISortedGroups() as $group}}
      <p class="ipsType_reset ipsType_medium">{$group->formattedName|raw}</p>
		{{endforeach}}
	{{endif}}
{{endif}}
',
          ),
        ),
      ), parent::hookData());
    }
    /* End Hook Data */
  }

