class hook168 extends _HOOK_CLASS_
{

    /* !Hook Data - DO NOT REMOVE */
public static function hookData() {
 return array_merge_recursive( array (
  'postContainer' => 
  array (
    0 => 
    array (
      'selector' => 'article > aside.ipsComment_author.cAuthorPane.ipsColumn.ipsColumn_medium.ipsResponsive_hidePhone > ul.cAuthorPane_info.ipsList_reset > li[data-role=\'group\']',
      'type' => 'add_after',
      'content' => '{{if \IPS\Member\Group::load( $comment->author()->member_group_id )->g_icon }}
	<li data-role=\'axen-group-icon\'>
    	<img src=\'{file="$comment->author()->group[\'g_icon\']" extension="core_Theme"}\' alt=\'\' class=\'cAuthorGroupIcon\' />
	</li>
{{endif}}

{{if settings.aXenGroupTopics}}
	{{if $comment->author()->aXenSGISortedGroups()}}
		{{foreach $comment->author()->aXenSGISortedGroups() as $group}}
				<li data-role="axen-group-secondary">{$group->formattedName|raw}</li>
				{{if $group->g_icon && settings.aXenGroupIcon}}
					<li data-role=\'axen-group-icon-secondary\'>
                    	<img src=\'{file="$group->g_icon" extension="core_Theme"}\' alt=\'\' class=\'cAuthorGroupIcon\' />
					</li>
				{{endif}}
		{{endforeach}}
	{{endif}}
{{endif}}',
    ),
    1 => 
    array (
      'selector' => 'article > aside.ipsComment_author.cAuthorPane.ipsColumn.ipsColumn_medium.ipsResponsive_hidePhone > ul.cAuthorPane_info.ipsList_reset > li[data-role=\'group-icon\']',
      'type' => 'replace',
      'content' => '',
    ),
  ),
), parent::hookData() );
}
/* End Hook Data */
}

