hooks.hook:
//<?php

	/* To prevent PHP errors (extending class does not exist) revealing path */
	if (!\defined('\IPS\SUITE_UNIQUE_KEY')) {
		exit;
	}

	class hook140 extends _HOOK_CLASS_
	{
		public function aXenSGISortedGroups()
		{
			try
			{
				try {
					$allGroups = \IPS\Member\Group::groups();
					$arrayGruups = explode(',', \IPS\Settings::i()->aXenGroup);
					$arrayGruupsFlip = array_flip(explode(',', $this->mgroup_others));
					$sortedGroups = array();
					$notSortedGroups = array();

					foreach ($arrayGruups as $id) {
						if (\in_array($id, explode(',', \IPS\Settings::i()->aXenGroupExclude))) unset($allGroups[$id]);
						if (isset($allGroups[$id]) and isset($arrayGruupsFlip[$id])) {
							$sortedGroups[$id] = $allGroups[$id];
							unset($arrayGruupsFlip[$id]);
						}
					}
					foreach ($arrayGruupsFlip as $sid => $i) {
						if (\in_array($sid, explode(',', \IPS\Settings::i()->aXenGroupExclude))) unset($allGroups[$sid]);
						if (isset($allGroups[$sid])) $notSortedGroups[$sid] = $allGroups[$sid];
					}
					return ($sortedGroups + $notSortedGroups);
				} catch (\RuntimeException $e) {
					if (method_exists(get_parent_class(), __FUNCTION__)) {
						return \call_user_func_array('parent::' . __FUNCTION__, \func_get_args());
					} else {
						throw $e;
					}
				}
			}
			catch ( \RuntimeException $e )
			{
				if ( method_exists( get_parent_class(), __FUNCTION__ ) )
				{
					return \call_user_func_array( 'parent::' . __FUNCTION__, \func_get_args() );
				}
				else
				{
					throw $e;
				}
			}
		}
	}

//<?php

  /* To prevent PHP errors (extending class does not exist) revealing path */
  if (!\defined('\IPS\SUITE_UNIQUE_KEY')) {
    exit;
  }

  class hook141 extends _HOOK_CLASS_
  {

    /* !Hook Data - DO NOT REMOVE */
    public static function hookData()
    {
      return array_merge_recursive(array(
        'member' =>
        array(
          0 =>
          array(
            'selector' => 'li.ipsStreamItem_member > div.ipsStreamItem_container > div.ipsStreamItem_header > p',
            'type' => 'add_after',
            'content' => '
{{if settings.aXenGroupSearch}}
  {{if $member->aXenSGISortedGroups()}}
    {{foreach $member->aXenSGISortedGroups() as $group}}
      <p class="ipsType_reset ipsType_medium">{$group->formattedName|raw}</p>
		{{endforeach}}
	{{endif}}
{{endif}}
',
          ),
        ),
      ), parent::hookData());
    }
    /* End Hook Data */
  }

